import React from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import { MDBDataTable } from 'mdbreact';
import DataTable from 'react-data-table-component';
import { Doughnut   } from 'react-chartjs-2';
// import { Doughnut } from 'react-chartjs-2';
import PieChart from 'react-minimal-pie-chart';
import ReactSvgPieChart from "react-svg-piechart"
const axios = require('axios');
const data = [
    {title: "Data 1", value: 100, color: "#22594e"},
    {title: "Data 2", value: 60, color: "#2f7d6d"},
    {title: "Data 3", value: 30, color: "#3da18d"},
    {title: "Data 4", value: 20, color: "#69c2b0"},
    {title: "Data 5", value: 10, color: "#a1d9ce"},
  ]
  const columnsMDB = [
    
    {
        label: 'userId',
        field: 'userId',
        sort: 'asc',
        width:150
    },
    {
        label: '#',
        field: 'id',
        sort: 'asc',
        width:100
    },
    {
      label: 'Title',
      field: 'title',
      sort: 'asc',
      width:300
    },
    {
      label: 'Body',
      field: 'body',
      sort: 'asc',
      width:600
    },
  ] ;

class Test extends React.Component{
    constructor(props){
        super(props);
        this.state={
            apiMDBvalue:null,
        }
    }
    componentDidMount(){
        this.apiData();
    }
    apiData = () =>{
        console.log("inside api data")
        axios({
            method: 'get',
            url: 'https://jsonplaceholder.typicode.com/posts',
            responseType: 'stream'
          })
        .then(  (response)=>{
            this.setState({
                apiValue: response.data,
                apiMDBvalue:{'rows': response.data}
            },()=>{
                console.log(this.state.apiValue)
            })
        });
    }
    render(){
        console.log("Inside test")
        return(
            <React.Fragment>
            <MDBDataTable
            striped
            bordered    
            hover
            paging={true}
            columns={columnsMDB}
            data={this.state.apiMDBvalue ? this.state.apiMDBvalue:[]}
        />
        <Doughnut
           data={this.state.apiMDBvalue ? this.state.apiMDBvalue:[]}
            width={100}
            height={50}
            // options={ {maintainAspectRatio: false} }
        />
            <PieChart
                animate={true}
                // rounded={true}
                width={60}
                x={10}
                startAngle={10}
                data={data}
            />;
            {/* <ReactSvgPieChart
                data={data}
                strokeWidth={.04}
                viewBoxSize={1}
                expandSize={1}
                // If you need expand on hover (or touch) effect
                expandOnHover
                // If you need custom behavior when sector is hovered (or touched)
                // onSectorHover={(d, i, e) => {
                // //   if (d) {
                // //     console.log("Mouse enter - Index:", i, "Data:", d, "Event:", e)
                // //   } else {
                // //     console.log("Mouse leave - Index:", i, "Event:", e)
                // //   }
                // }}
            /> */}

        </React.Fragment>
        );
    }
}

export default Test;
