import React from 'react'
import Header from './header.js'
import Footer from './footer.js'
// import SimpleReactValidator from 'simple
import Form from 'react-validation/build/form';
import Input from 'react-validation/build/input';//-react-validator';
import validator from 'validator';
const required = (value) => {
    if (!value.toString().trim().length) {
      // We can return string or jsx as the 'error' prop for the validated Component
      return 'require';
    }
  };
   
  const email = (value) => {
    if (!validator.isEmail(value)) {
      return `${value} is not a valid email.`
    }
  };
class Contact extends React.Component{
    constructor(props) {
        super(props)
        // this.validator = new SimpleReactValidator();
    }
    
    render(){
        return(
            <React.Fragment>
                <Header/>
                <h1>/</h1>
                {/*Section: Contact v.2*/}
                <div className="contact-form">
                    <div className="container">
                    <section className="mb-4">
                        {/*Section heading*/}
                        <h2 className="h1-responsive font-weight-bold text-center my-4">Contact us</h2>
                        {/*Section description*/}
                        <p className="text-center w-responsive mx-auto mb-5">Do you have any questions? Please do not hesitate to contact us directly. Our team will come back to you within
                        a matter of hours to help you.</p>
                        <div className="row">
                        {/*Grid column*/}
                        <div className="col-md-9 mb-md-0 mb-5">
                            <form id="contact-form" name="contact-form" action="mail.php" method="POST">
                            {/*Grid row*/}
                            <div className="row">
                                {/*Grid column*/}
                                <div className="col-md-6">
                                <div className="md-form mb-0">
                                    <input type="text" id="name" name="name" placeholder="Please Enter Your Name" className="form-control" />
                                    
                                </div>
                                </div>
                                {/*Grid column*/}
                                {/*Grid column*/}
                                <div className="col-md-6">
                                <div className="md-form mb-0">
                                    <input type="text" id="email" name="email"  placeholder="Please Enter Your Email Id" validations={[required, email]} className="form-control"/>
                                    {this.errors}
                                </div>
                                </div>
                                {/*Grid column*/}
                            </div>
                            {/*Grid row*/}
                            {/*Grid row*/}
                            <div className="row">
                                <div className="col-md-12">
                                <div className="md-form mb-0">
                                    <input type="text" id="subject"  placeholder="Please Enter Your Subject" name="subject" className="form-control" />
                                </div>
                                </div>
                            </div>
                            {/*Grid row*/}
                            {/*Grid row*/}
                            <div className="row">
                                {/*Grid column*/}
                                <div className="col-md-12">
                                <div className="md-form">
                                    <textarea type="text" id="message" name="message" placeholder="Please Enter Your message" rows={2} className="form-control md-textarea" defaultValue={""} />
                                </div>
                                </div>
                            </div>
                            {/*Grid row*/}
                            </form>
                            <div className="text-center text-md-left">
                            <a className="btn btn-primary" onclick="document.getElementById('contact-form').submit();">Send Message</a>
                            </div>
                            <div className="status" />
                        </div>
                        {/*Grid column*/}
                        {/*Grid column*/}
                        <div className="col-md-3 text-center">
                            <ul className="list-unstyled mb-0">
                            <li><i className="fas fa-map-marker-alt fa-2x" />
                                <p>Rajajinagar, West of Chord road, Bangalore 560086, India</p>
                            </li>
                            <li><i className="fas fa-phone mt-4 fa-2x" />
                                <p>+ 01 234 567 89</p>
                            </li>
                            <li><i className="fas fa-envelope mt-4 fa-2x" />
                                <p>info@redintegro.com</p>
                            </li>
                            </ul>
                        </div>
                        {/*Grid column*/}
                        </div>
                    </section>
                    
                    </div>

                </div>
      {/*Section: Contact v.2*/}
                <Footer/>
            </React.Fragment>
        )
    }
}
export default Contact;
