import React from 'react'
class Home extends React.Component{
    constructor(props){
        super(props)
        this.state = {
           val: "Prakash" ,
           flag:1,
           users:[],
           name:'',
        }
     }
    check = ()=>{
        const flag=this.state.flag;
        console.log("flag" +flag)
        if(flag==1){
            this.setState({
                val:"Hello You clicked",
                flag:0
            })
        }else{
            this.setState({
                val:"Click",
                flag:1
            })
        }
        }
        addUser = () => {
            this.setState((prevState)=>{
                return {
                    users : [...prevState.users, prevState.name],
                    name: ""
                }
            })
        }
        handleChange(event) {
            this.setState({name: event.target.value})
        }
    render(){
        var val="prakash";
        const flag=this.state;
        console.log(this.state.users)
        return(
            <React.Fragment>
                <h1>Hello World</h1>
                <div>
                    <input type="button" value="Click" onClick={this.check}/>
                    <h1>hello Wolrd {this.state.val}</h1>
                </div>
                <input type="text"  name="userName" value={this.state.name} onChange={this.handleChange.bind(this)}/> <input type="button" onClick={this.addUser} value="add User"/>
                { this.state.users.map((user,index)=>(
                   <h1 key={index}>{user}</h1>
               ))}
            </React.Fragment>
        );       
    }
}
export default Home;
