class Home extends React.Component{
    constructor(props){
        super(props)
        this.state = {
           val: "Prakash" ,
           flag:1,
           users:[],
           name:'',
        }
     }
    check = ()=>{
        const flag=this.state.flag;
        console.log("flag" +flag)
        if(flag==1){
            this.setState({
                val:"Hello You clicked",
                flag:0
            })
        }else{
            this.setState({
                val:"Click",
                flag:1
            })
        }
       
        }
        addUser = () => {
            this.state.users.push(this.state.name)
            const usr=this.state.users;
            console.log("ok ",usr)
            this.state.name='';
        }
        handleChange(event) {
            this.setState({name: event.target.value})
            this.state.name='';
        }
