import React from 'react'
import Header from './header.js'
import Footer from './footer.js'
import SimpleReactValidator from 'simple-react-validator';
import Form from 'react-validation/build/form';
import Input from 'react-validation/build/input';//-react-validator';
class Contact extends React.Component{
    constructor(props) {
        super(props)
        this.state = {
            email: "",
            name:'',
            subject:'',
            message:''
        }
        this.validator = new SimpleReactValidator({autoForceUpdate: this,messages: {
            email: 'That is not an E mail!!.',
            // OR
            default: 'Validation has failed!'  // will override all messages
          },element: message => <div style={{color: "red"}}>{message}</div>});
    }
    
    render(){
       
        console.log(this.state.email)
        return(
            <React.Fragment>
                <Header/>
                <h1>/</h1>
                {/*Section: Contact v.2*/}
                <div className="contact-form">
                    <div className="container">
                    <section className="mb-4">
                        {/*Section heading*/}
                        <h2 className="h1-responsive font-weight-bold text-center my-4">Contact us</h2>
                        {/*Section description*/}
                        <p className="text-center w-responsive mx-auto mb-5">Do you have any questions? Please do not hesitate to contact us directly. Our team will come back to you within
                        a matter of hours to help you.</p>
                        <div className="row">
                        {/*Grid column*/}
                        <div className="col-md-9 mb-md-0 mb-5">
                            <Form id="contact-form" name="contact-form" action="mail.php" method="POST">
                            {/*Grid row*/}
                            <div className="row">
                                {/*Grid column*/}
                                <div className="col-md-6">
                                <div className="md-form mb-0">
                                    <input type="text" id="name" name="name"  value={this.state.name} onChange={(e)=>{
                                        this.setState({
                                            name:e.target.value
                                        })
                                    }}   onBlur={() => this.validator.showMessageFor('name')}placeholder="Please Enter Your Name" className="form-control" />
                                     {this.validator.message('name', this.state.name, 'required')}
                                </div>
                                </div>
                                {/*Grid column*/}
                                {/*Grid column*/}
                                <div className="col-md-6">
                                <div className="md-form mb-0">
                                    <input type="text" id="email" name="email" value={this.state.email}  onChange={(e)=>{
                                        this.setState({
                                            email: e.target.value
                                        })
                                    }} onBlur={() => this.validator.showMessageFor('email')} placeholder="Please Enter Your Email Id" className="form-control"/>
                                    
                                    {this.validator.message('email', this.state.email, 'required|email')}

                                </div>
                                </div>
                                {/*Grid column*/}
                            </div>
                            {/*Grid row*/}
                            {/*Grid row*/}
                            <div className="row">
                                <div className="col-md-12">
                                <div className="md-form mb-0">
                                    <input type="text" id="subject"  value={this.state.subject} onChange={(e)=>{
                                        
                                    }} placeholder="Please Enter Your Subject" name="subject" className="form-control" />
                                </div>
                                </div>
                            </div>
                            {/*Grid row*/}
                            {/*Grid row*/}
                            <div className="row">
                                {/*Grid column*/}
                                <div className="col-md-12">
                                <div className="md-form">
                                    <textarea type="text" id="message" name="message" placeholder="Please Enter Your message" rows={2} className="form-control md-textarea" defaultValue={""} />
                                </div>
                                </div>
                            </div>
                            {/*Grid row*/}
                            </Form>;
                            <div className="text-center text-md-left">
                            <a className="btn btn-primary" onclick="document.getElementById('contact-form').submit();">Send Message</a>
                            </div>
                            <div className="status" />
                        </div>
                        {/*Grid column*/}
                        {/*Grid column*/}
                        <div className="col-md-3 text-center">
                            <ul className="list-unstyled mb-0">
                            <li><i className="fas fa-map-marker-alt fa-2x" />
                                <p>Rajajinagar, West of Chord road, Bangalore 560086, India</p>
                            </li>
                            <li><i className="fas fa-phone mt-4 fa-2x" />
                                <p>+ 01 234 567 89</p>
                            </li>
                            <li><i className="fas fa-envelope mt-4 fa-2x" />
                                <p>info@redintegro.com</p>
                            </li>
                            </ul>
                        </div>
                        {/*Grid column*/}
                        </div>
                    </section>
                    
                    </div>

                </div>
      {/*Section: Contact v.2*/}
                <Footer/>
            </React.Fragment>
        )
    }
}
export default Contact;
