    //         }
    // })
        // this.val="Hello, Prakash"
        // console.log("hello World"+this.val);
    render(){
        var val="prakash";
        const flag=this.state;
        console.log("tested" ,flag)
        return(
            <React.Fragment>
                <h1>Hello World</h1>
                <div>
                    <input type="button" value="Click" onClick={this.check}/>
                    <h1>hello Wolrd {this.state.val}</h1>
                </div>
                <input type="text"  name="userName" value={this.state.name}/> <input type="button" onClick={this.addUser} value="add User"/>
            </React.Fragment>
        );       
    }
}
export default Home;
