import React from 'react'
import { Component } from 'ag-grid-community';
import Header from './header.js'
import Footer from './footer.js'
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css"
import Loader from 'react-loader-spinner'
// import React from 'react';
import { Slide } from 'react-slideshow-image';
const slideImages = [
    'https://images.alphacoders.com/665/thumb-1920-665091.jpg',
    'https://images.unsplash.com/photo-1515879218367-8466d910aaa4?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&w=1000&q=80',
    'http://wpshopmart.com/inc/uploads/2016/10/Inspirational-Code-HD-Wallpapers.jpg'
  ];
  const properties = {
    duration: 5000,
    transitionDuration: 500,
    infinite: true,
    height:400,
    indicators: true,
    arrows: true
  }
class Admin extends React.Component{
    constructor(props) {
        super(props)
        this.state = { isLoading: true }
    }
    
    componentDidMount() {
        setTimeout(()=>{
            this.setState({
                isLoading: false
            })
        },3000)
    }
    render(){
        return(
            <React.Fragment>
                {/* <Header/> */}
                 <h1>Main File </h1>
                 <Slide {...properties}>
                    <div className="each-slide">
                    <div style={{'backgroundImage': `url(${slideImages[0]})` ,'height':600,'background-size': 'cover'}}>
                        <span>Slide 1</span>
                    </div>
                    </div>
                    <div className="each-slide">
                    <div style={{'backgroundImage': `url(${slideImages[1]})` ,'height':600,'background-size': 'cover'}}>
                        <span>Slide 2</span>
                    </div>
                    </div>
                    <div className="each-slide">
                    <div style={{'backgroundImage': `url(${slideImages[2]})` ,'height':600,'background-size': 'cover'}}>
                        <span>Slide 3</span>
                    </div>
                    </div>
                </Slide>
                    <div className="center_loader">
                    {this.state.isLoading ?   <Loader
                                            type="Puff"
                                            color="#00BFFF"
                                            height={100}
                                            width={100}
                                            timeout={3000} //3 secs
                                        /> : ''}
                    </div>
                 
                <Footer />
            </React.Fragment>
        )
    }
}
export default Admin;
