import React from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
class Home extends React.Component{
    constructor(props){
        super(props)
        this.state = {
           val: "Prakash" ,
           flag:1,
           users:[],
           name:'',
           err:''
        }
     }
    check = ()=>{
        const flag=this.state.flag;
        console.log("flag" +flag)
        if(flag==1){
            this.setState({
                val:"Hello You clicked",
                flag:0
            })
        }else{
            this.setState({
                val:"Click",
                flag:1
            })
        }
        }
        addUser = () => {
            this.setState((prevState)=>{
                if(prevState.name==''){
                   return{
                    err:'Please Enter Name'
                   } 
                }else{
                    return {
                        users : [...prevState.users, prevState.name],
                        name: "",
                        err:''
                    }
                }
            })
        }
        handleChange(event) {
            this.setState({name: event.target.value})
        }
    render(){
        var val="prakash";
        // const flag=this.state;
        // console.log(this.state.users)
        return(
            <React.Fragment>
                <h1>Hello World</h1>
                <div>
                    <input type="button"  class="btn btn-info" value="Click" onClick={this.check}/>
                    <h1>hello Wolrd {this.state.val}</h1>
                </div>
                <div class="conatiner">
                    <div class="row">
                        <div class="col-md-3">
                        
                        </div>
                        <div class="col-md-3">
                            <input type="text" class="form-control" name="userName" value={this.state.name} onChange={this.handleChange.bind(this)}/> 
                        </div>
                        <div class="col-md-3">
                            <input type="button" onClick={this.addUser} class="btn btn-primary" value="add User"/>
                        </div>
                    </div>
                    
                </div>
              <h3> {this.state.err}</h3> 
                { this.state.users.map((user,index)=>(
                   <h1 key={index}>{user}</h1>
               ))}
            </React.Fragment>
        );       
    }
}
export default Home;
