import React from 'react'
class Home extends React.Component{
    constructor(props){
        var flag=0;
        super(props)
        this.state = {
           val: "Prakash" ,
           flag:1,
        }
     }
    check = ()=>{
        console.log("flag" +this.flag)
        if(flag==1){
            this.setState({
                val:"Hello You clicked",
                flag:0
            })
        }else{
            this.setState({
                val:"Click",
                flag:1
            })
        }
        
