    render(){
        var val="prakash";
        return(
            <React.Fragment>
                <h1>Hello World</h1>
                <div>
                    <input type="button" value="Click" onClick={this.check}/>
                    <h1>hello Wolrd {this.state.val}</h1>
                </div>
            </React.Fragment>
        );       
    }
}
export default Home;
